import 'package:carousel_slider/carousel_slider.dart';
import 'package:flutter/cupertino.dart';
import 'package:flutter/material.dart';

import 'CustomNetworkImage.dart';

class Carousel extends StatefulWidget {
  final List img;
  final double hight, raduis;
  final bool isProduct;
  final BoxFit fit;

  const Carousel({Key key, this.img, this.hight, this.isProduct, this.raduis, this.fit}) : super(key: key);

  @override
  _CarouselState createState() => _CarouselState();
}

class _CarouselState extends State<Carousel> {
  List empty = [
    // "data:image/jpeg;base64,",
    // "https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.wired.com%2Fstory%2Fjd-power-korean-cars-beat-us-europe-quality%2F&psig=AOvVaw2jB-_jrUrbJGaPjjoOb0m5&ust=1615157253278000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCOjZouffnO8CFQAAAAAdAAAAABAD",
    // "https://advergizefiles-bragboxx.netdna-ssl.com/wp-content/uploads/ANLCI-3.jpg"
  ];

  @override
  Widget build(BuildContext context) {
    return Padding(
      padding: const EdgeInsets.symmetric(vertical: 10),
      child: CarouselSlider(
        options: CarouselOptions(
          height: MediaQuery.of(context).size.height / 5,
          autoPlay: true,
          aspectRatio: 1.0,
          enlargeCenterPage: true,
        ),
        items: widget.img.isEmpty
            ? empty.map(
                (url) {
                  return Stack(
                    children: [
                      CustomNetworkImage().containerNewWorkImage(
                          radius: 50,
                          fit: BoxFit.cover,
                          width: double.infinity,
                          height: widget.hight ?? MediaQuery.of(context).size.height / 5,
                          image: url),
                      widget.isProduct != false
                          ? Container(
                              width: double.infinity,
                              height: widget.hight ?? MediaQuery.of(context).size.height / 10,
                              child: Center(
                                child: Text(
                                  "عروض و خصومات لفترة محدودة",
                                  style: TextStyle(color: Colors.white, fontSize: 18),
                                ),
                              ),
                              decoration: BoxDecoration(
                                  // borderRadius: BorderRadius.circular(5),
                                  color: Colors.orange.withOpacity(0.6)),
                            )
                          : SizedBox()
                    ],
                  );
                },
              ).toList()
            : widget.img.map(
                (url) {
                  return InkWell(
                    onTap: () {
                      if (widget.isProduct == false) {
                        showImageDialog(url);
                      } else {
                        // navigate();
                      }
                    },
                    child: CustomNetworkImage().containerNewWorkImage(
                        width: double.infinity,
                        height: widget.hight ?? MediaQuery.of(context).size.height / 5,
                        radius: 12,
                        image: url,
                        fit: widget.fit ?? BoxFit.cover),
                  );
                },
              ).toList(),
      ),
    );
  }

  void showImageDialog(String image) {
    showDialog(
        context: context,
        builder: (BuildContext context) {
          return Dialog(
            backgroundColor: Colors.black.withOpacity(0.9),
            elevation: 200,
            child: Image.network(
              image,
              fit: BoxFit.cover,
              width: double.infinity,
              height: 300,
            ),
          );
        });
  }
}
